sp_angular_provider.script:
function($rootScope, $http){

	$rootScope.$on('get', function(evt,data){

		$http.get(data.url + data.tableName+'?sysparm_query='+data.query+'&sysparm_limit='+data.queryLimit).then(
			function successCallback(response) {
			$rootScope.$broadcast('records.queried', response.data.result);
		}, function errorCallback(response) {
			console.log("Unable to perform get request");
			$rootScope.$broadcast('error',response);
		});

	});

	$rootScope.$on('getById', function(evt,data){

		$http.get(data.url+data.tableName+'/'+data.id).then(
      function successCallback(response) {
				$rootScope.$broadcast('record.queried', response.data.result);
      },
      function errorCallback(response) {
        console.log("Unable to perform get request");
			  $rootScope.$broadcast('error', response);
      }
    );

	});

	$rootScope.$on('post', function(evt,data) {

    $http.post(data.url + data.tableName, data.body).then(
      function successCallback(response) {
        console.log("Successfully POST-ed data");
				$rootScope.$broadcast('record.created', response);
      },
      function errorCallback(response) {
        console.log("POST-ing of data failed");
			  $rootScope.$broadcast('error', response);
      }
    );

	});

	$rootScope.$on('patch', function(evt,data) {

		$http.patch(data.url + data.tableName + '/'+ data.body.sys_id, data.body).then(
      function successCallback(response) {
        console.log("Successfully PATCH-ed data");
				$rootScope.$broadcast('record.updated', response);
      },
      function errorCallback(response) {
        console.log("PATCH-ing of data failed");
				$rootScope.$broadcast('error', response);
      }
    );

	});

	$rootScope.$on('reduce', function(data, modelGroup){
		var obj = {};
		modelGroupKeys.map(function(d){
			obj[d] = data[d];
		});
		return obj;
	});

}
